let controls=document.querySelectorAll(".controls-btn"),slides=document.querySelectorAll(".slider-item"),resetCurrentSlides=()=>{slides.forEach(e=>{e.classList.remove("slide-current")}),controls.forEach(e=>{e.classList.remove("current")})};controls.forEach((e,s)=>{e.addEventListener("click",()=>{resetCurrentSlides(),slides[s].classList.add("slide-current"),e.classList.add("current")})});